package practice;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte ch=65;
		byte ch1=97;
		System.out.println((char)ch);
		for(int i=0;i< 26;i++) {
			System.out.print ((char)(ch+i));
		}
		System.out.println();
		for(int i=0;i< 26;i++) {
			System.out.print ((char)(ch1+i));
			
		}
	}

}
